DROP TABLE IF EXISTS paths;

CREATE TABLE paths (
      id SERIAL,
      trail_id int NOT NULL,
      date bigint,
      user_id int DEFAULT NULL,
      rating int DEFAULT 0,
      comment text,
      tag text,
      gpx_id int, 
      gpx_url text,
      is_hero_path boolean NOT NULL DEFAULT FALSE,
      PRIMARY KEY (id)
);


